


CREATE TABLE master_data(
   productID    INTEGER  NOT NULL PRIMARY KEY 
  ,productName  VARCHAR(54) NOT NULL
  ,productPrice VARCHAR(255) NOT NULL
  ,supplierID   INTEGER  NOT NULL
  ,supplierName VARCHAR(30) NOT NULL
  ,storeID      INTEGER  NOT NULL
  ,storeName    VARCHAR(12) NOT NULL
);




DELETE FROM master_data
WHERE productID IS NULL 
  AND productName IS NULL 
  AND productPrice IS NULL 
  AND supplierID IS NULL 
  AND supplierName IS NULL 
  AND storeID IS NULL 
  AND storeName IS NULL;

-- Add a new column to temporarily store the updated price
ALTER TABLE master_data
ADD COLUMN newProductPrice DECIMAL(10,2);

-- Update the new column with the corrected price values
UPDATE master_data
SET newProductPrice = CAST(REPLACE(productPrice, '$', '') AS DECIMAL(10,2));

-- Drop the old column
ALTER TABLE master_data
DROP COLUMN productPrice;

-- Rename the new column to the original name
ALTER TABLE master_data
CHANGE COLUMN newProductPrice productPrice DECIMAL(10,2);